import React, { useState, useEffect } from "react";
import axios from "axios";
import { BalldontlieAPI } from "@balldontlie/sdk";

const NBABettingApp = () => {
  const [games, setGames] = useState([]);
  const [topPlayers, setTopPlayers] = useState([]);
  const api = new BalldontlieAPI({ apiKey: "dae8ef55-6309-4191-aefc-29dc0ac60023" });

  useEffect(() => {
    const fetchGames = async () => {
      try {
        // Fetch games for today (you can replace the date with the current date dynamically)
        const today = new Date().toISOString().split("T")[0];  // Format as YYYY-MM-DD
        const response = await api.nba.getGames({
          start_date: today,
          end_date: today
        });

        setGames(response.data);  // Set the games data
      } catch (error) {
        console.error("Error fetching games:", error);
      }
    };

    fetchGames();
  }, []);

  useEffect(() => {
    const fetchTopPlayers = async () => {
      try {
        const playersData = [];

        for (let game of games) {
          // Fetch the teams in each game
          const team1 = await api.nba.getTeam(game.home_team);
          const team2 = await api.nba.getTeam(game.visitor_team);

          // Fetch top players for each team
          const team1TopPlayers = await api.nba.getPlayers({ team_id: team1.id });
          const team2TopPlayers = await api.nba.getPlayers({ team_id: team2.id });

          playersData.push({
            team1: {
              name: team1.data.name,
              topPlayers: team1TopPlayers.data.slice(0, 3)  // Top 3 players
            },
            team2: {
              name: team2.data.name,
              topPlayers: team2TopPlayers.data.slice(0, 3)  // Top 3 players
            }
          });
        }

        setTopPlayers(playersData);
      } catch (error) {
        console.error("Error fetching top players:", error);
      }
    };

    if (games.length > 0) {
      fetchTopPlayers();
    }
  }, [games]);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">NBA Betting Insights</h1>

      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Today's Games</h2>
        {games.length > 0 ? (
          games.map((game, index) => (
            <div key={index} className="mb-4">
              <p>
                {game.home_team} vs {game.visitor_team}
              </p>
              <p>Game Time: {game.date}</p>
            </div>
          ))
        ) : (
          <p>No games scheduled for today.</p>
        )}
      </div>

      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-2">Top Players</h2>
        {topPlayers.length > 0 ? (
          topPlayers.map((game, index) => (
            <div key={index} className="mb-4">
              <h3>{game.team1.name} vs {game.team2.name}</h3>
              <div>
                <strong>{game.team1.name} Top Players:</strong>
                <ul>
                  {game.team1.topPlayers.map(player => (
                    <li key={player.id}>{player.first_name} {player.last_name}</li>
                  ))}
                </ul>
              </div>
              <div>
                <strong>{game.team2.name} Top Players:</strong>
                <ul>
                  {game.team2.topPlayers.map(player => (
                    <li key={player.id}>{player.first_name} {player.last_name}</li>
                  ))}
                </ul>
              </div>
            </div>
          ))
        ) : (
          <p>No top players data available.</p>
        )}
      </div>
    </div>
  );
};

export default NBABettingApp;
